import torch
import base64
import platform
from io import BytesIO
import torch.cuda as cuda
from flask import Flask, request
from diffusers import StableDiffusionPipeline
from diffusers.pipelines.stable_diffusion import safety_checker

model_id = "CompVis/stable-diffusion-v1-4"
dtype = torch.float16
prompt_framework = "UI design simple objects flat design corporate modern icon vector clean lines %PROMPT%"
negative_prompt = "multiple abstract background blurry blurred distorted"
error_image = ""
use_attention_slicing = False
image_encoding = "data:image/jpeg;base64, %DATA%"

pipe = StableDiffusionPipeline.from_pretrained(model_id, torch_dtype=dtype, safety_checker=None,
                                               requires_safety_checker=False)


def sc_helper(images):
  return images, [False for _ in images]


def sc(self, _, images):
  return sc_helper(images)


# edit the StableDiffusionSafetyChecker class so that, when called, it just returns the images and an array of True values
safety_checker.StableDiffusionSafetyChecker.forward = sc
pipe.safety_checker = lambda images, _: sc_helper(images)

if cuda.is_available():
  print("Device: CUDA")
  pipe.to("cuda")
elif platform.system() == "Darwin":
  print("Device: MPS")
  pipe.to("mps")
else:
  print("Device: CPU")
  pipe.to("cpu")

if use_attention_slicing:
  pipe.enable_attention_slicing()

app = Flask(__name__)


@app.route("/")
def test():
  return app.send_static_file("test.html")


def generate_image(prompt):
  image = pipe(prompt).images[0]
  buffered = BytesIO()
  image.save(buffered, format="JPEG")
  encoded = base64.b64encode(buffered.getvalue())
  return encoded


@app.route("/generate")
def generate():
  prompt = request.args.get("prompt", None)
  if (prompt is None):
    raise ValueError("No prompt")
  prompt = prompt_framework.replace("%PROMPT%", prompt)
  image = generate_image(prompt)
  if image == error_image:
    raise RuntimeError("Error generating image")
  return image_encoding.replace("%DATA%", image.decode("utf-8"))

if __name__ == "__main__":
  app.run()
